/**
 * Function to calculate the sum of two matrices represented as two dimensional arrays.
 *
 * @param matrix1 The first matrix
 * @param matrix2 The second matrix
 * @returns The resultant matrix containing the sum of the two input matrices.
 * @throws An error if the input matrices have different dimensions.
 */
function sumOfMatrices(matrix1: number[][], matrix2: number[][]) {
  const rows1 = matrix1.length;
  const cols1 = matrix1[0].length;
  const rows2 = matrix2.length;
  const cols2 = matrix2[0].length;

  if (rows1 !== rows2 || cols1 !== cols2) {
    throw new Error("Input matrices should have the same dimensions.");
  }

  const result: number[][] = [];
  for (let I = 0; I < rows1; i++) {
    result[i] = [];
    for (let j = 0; j < cols1; j++) {
      result[i][j] = matrix1[i][j] + matrix2[i][j];
    }
  }
  return result;
}

